
public class LargestOfThree {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=44,b=24,c=42;
		if(a>=b && a>=c)
			System.out.println(a+" is Largest");
		else if(b>=c)
			System.out.println(b+" is Largest");
		else
			System.out.println(c+" is Largest");

	}

}
